package taller_clase_5;

class punto5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1= 20;
		int num2= 5;
		
		int suma, resta, producto, division;
		
		suma= num1 + num2;
		resta= num1 - num2;
		producto= num1 * num2;
		division= num1 / num2;
		
		
		System.out.println("la suma de los numeros dados es: "+suma+" , su resta: "+resta+", su producto: "+producto+" y su division: "+division);

	}

}
